import React, { useState, useEffect } from 'react';
import Modal from './Modal';
import './Customer.css';

const CustomerList = () => {
  const [selectedCustomer, setSelectedCustomer] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [customers, setCustomers] = useState([]);

  useEffect(() => {
    const fetchCustomers = async () => {
      try {
        const response = await fetch('../assets/sample_customers.csv');
        const csvData = await response.text();
        const parsedData = parseCSVData(csvData);
        setCustomers(parsedData);
      } catch (error) {
        console.error('Error fetching customer data:', error);
      }
    };

    fetchCustomers();
  }, []);

  const parseCSVData = (csvData) => {
    const lines = csvData.split('\n');
    const headers = lines[0].split(',');
    const customerData = lines.slice(1).map((line) => {
      const values = line.split(',');
      return Object.fromEntries(headers.map((header, index) => [header.trim(), values[index].trim()]));
    });
    return customerData;
  };

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const filteredCustomers = customers.filter((customer) =>
    customer.phoneNumber.includes(searchTerm)
  );

  const handleViewDetails = (customer) => {
    setSelectedCustomer(customer);
    setIsModalOpen(true);
  };

  const handleModalClose = () => {
    setIsModalOpen(false);
  };

  const handleSaveCustomer = (updatedCustomer) => {
    setCustomers((prevCustomers) => {
      const updatedCustomers = prevCustomers.map((customer) =>
        customer.id === updatedCustomer.id ? updatedCustomer : customer
      );
      return updatedCustomers;
    });

    setIsModalOpen(false);
  };

  return (
    <div>
      <div>
        <h2>Customer List</h2>
        <input
          type="text"
          placeholder="Search by phone number"
          value={searchTerm}
          onChange={handleSearch}
        />
      </div>
      <table className="customer-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Address</th>
            <th>Phone Number</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {filteredCustomers.map((customer) => (
            <tr key={customer.id}>
              <td>{customer.id}</td>
              <td>{customer.name}</td>
              <td>{customer.address}</td>
              <td>{customer.phoneNumber}</td>
              <td>
                <button onClick={() => handleViewDetails(customer)}>View/Edit</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {isModalOpen && selectedCustomer && (
        <Modal onClose={handleModalClose} customer={selectedCustomer} onSave={handleSaveCustomer}>
          <h3>Customer Details</h3>
          <p>ID: {selectedCustomer.id}</p>
          <p>Name: {selectedCustomer.name}</p>
          <p>Address: {selectedCustomer.address}</p>
          <p>Phone Number: {selectedCustomer.phoneNumber}</p>
          {/* Add editable fields and update logic here */}
        </Modal>
      )}
    </div>
  );
};

export default CustomerList;
