import React, { useState, useEffect } from 'react';
import Modal from './Modal';
import './Customer.css';

const CustomerList = () => {
    const [selectedCustomer, setSelectedCustomer] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [searchTerm, setSearchTerm] = useState('');
    const [customers, setCustomers] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const itemsPerPage = 10;

    useEffect(() => {
        fetch('../assets/sample_customers.json')
            .then(response => response.json())
            .then(data => {
                console.log('Fetched data:', data);  // Log the raw fetched data
                const transformedData = data.customers.map((customer, index) => ({
                    id: index + 1,
                    name: `${customer.firstName} ${customer.lastName}`,
                    address: customer.address,
                    phoneNumber: customer.phoneNumber
                }));

                console.log('Transformed data:', transformedData);  // Log the transformed data
                setCustomers(transformedData);
            })
            .catch(err => {
                console.error('Error fetching data:', err);  // Log any errors
            });
    }, []);



    const handleSearch = (event) => {
        setSearchTerm(event.target.value);
    };

    const filteredCustomers = customers.filter((customer) =>
        customer.phoneNumber.includes(searchTerm)
    );

    const indexOfLastItem = currentPage * itemsPerPage;
    const indexOfFirstItem = indexOfLastItem - itemsPerPage;
    const currentItems = filteredCustomers.slice(indexOfFirstItem, indexOfLastItem);

    const handleViewDetails = (customer) => {
        setSelectedCustomer(customer);
        setIsModalOpen(true);
    };

    const handleModalClose = () => {
        setIsModalOpen(false);
    };

    const handleSaveCustomer = (updatedCustomer) => {
        setCustomers((prevCustomers) => {
            const updatedCustomers = prevCustomers.map((customer) =>
                customer.id === updatedCustomer.id ? updatedCustomer : customer
            );
            return updatedCustomers;
        });

        setIsModalOpen(false);
    };

    const goToNextPage = () => {
        setCurrentPage((prevPage) => prevPage + 1);
    };

    const goToPreviousPage = () => {
        setCurrentPage((prevPage) => prevPage - 1);
    };

    return (
        <div>
            <div>
                <h2>Customer List</h2>
                <input
                    type="text"
                    placeholder="Search by phone number"
                    value={searchTerm}
                    onChange={handleSearch}
                />
            </div>
            <table className="customer-table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Address</th>
                        <th>Phone Number</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {currentItems.map((customer) => (
                        <tr key={customer.id}>
                            <td>{customer.id}</td>
                            <td>{customer.name}</td>
                            <td>{customer.address}</td>
                            <td>{customer.phoneNumber}</td>
                            <td>
                                <button onClick={() => handleViewDetails(customer)}>View/Edit</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <div className="pagination-container">
  <div>
    {currentPage > 1 ? (
      <button onClick={goToPreviousPage}>&lt;</button>
    ) : (
      <button disabled>&lt;</button>
    )}
    {filteredCustomers.length > currentPage * itemsPerPage ? (
      <button onClick={goToNextPage}>&gt;</button>
    ) : (
      <button disabled>&gt;</button>
    )}
  </div>
</div>


            {isModalOpen && selectedCustomer && (
                <Modal onClose={handleModalClose} customer={selectedCustomer} onSave={handleSaveCustomer}>
                    <h3>Customer Details</h3>
                    <p>ID: {selectedCustomer.id}</p>
                    <p>Name: {selectedCustomer.name}</p>
                    <p>Address: {selectedCustomer.address}</p>
                    <p>Phone Number: {selectedCustomer.phoneNumber}</p>
                    {/* Add editable fields and update logic here */}
                </Modal>
            )}
        </div>
    );
};

export default CustomerList;
