import React, { useState } from 'react';
import { connect } from 'react-redux'; // Import connect
import Modal from './Modal';
import './Customer.css';

// Import any actions or action creators you may need

const CustomerList = ({ customers, setCustomers }) => { // Receive customers and setCustomers from Redux store
  const [selectedCustomer, setSelectedCustomer] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const filteredCustomers = customers.filter(customer =>
    customer.phoneNumber.includes(searchTerm)
  );

  const handleViewDetails = (customer) => {
    setSelectedCustomer(customer);
    setIsModalOpen(true);
  };

  const handleModalClose = () => {
    setIsModalOpen(false);
  };

  const handleSaveCustomer = (updatedCustomer) => {
    const index = customers.findIndex((customer) => customer.id === updatedCustomer.id);

    if (index !== -1) {
      const updatedCustomers = [
        ...customers.slice(0, index),
        updatedCustomer,
        ...customers.slice(index + 1)
      ];

      setSelectedCustomer(updatedCustomer);
      setCustomers(updatedCustomers); // Dispatch action to update customers in Redux store
    }

    setIsModalOpen(false);
  };

  return (
    <div>
      <h2>Customer List</h2>
      <input
        type="text"
        placeholder="Search by phone number"
        value={searchTerm}
        onChange={handleSearch}
      />
      <table className="customer-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Address</th>
            <th>Phone Number</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {filteredCustomers.map(customer => (
            <tr key={customer.id}>
              <td>{customer.id}</td>
              <td>{customer.name}</td>
              <td>{customer.address}</td>
              <td>{customer.phoneNumber}</td>
              <td>
                <button onClick={() => handleViewDetails(customer)}>View/Edit</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {isModalOpen && selectedCustomer && (
        <Modal onClose={handleModalClose} customer={selectedCustomer} onSave={handleSaveCustomer}>
          <h3>Customer Details</h3>
          <p>ID: {selectedCustomer.id}</p>
          <p>Name: {selectedCustomer.name}</p>
          <p>Address: {selectedCustomer.address}</p>
          <p>Phone Number: {selectedCustomer.phoneNumber}</p>
          {/* Add editable fields and update logic here */}
        </Modal>
      )}
    </div>
  );
};

// Define mapStateToProps function to map Redux state to component props
const mapStateToProps = (state) => {
  return {
    customers: state.customers // Assuming 'customers' is the relevant key in your Redux store
  };
};

// Define mapDispatchToProps function to map actions or action creators to component props
const mapDispatchToProps = (dispatch) => {
  return {
    setCustomers: (updatedCustomers) => dispatch({ type: 'SET_CUSTOMERS', payload: updatedCustomers }) // Dispatch an action to update customers
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(CustomerList); // Connect the component to Redux
