import React, { useState } from 'react';
import Modal from './Modal';
import './Customer.css';

const CustomerList = () => {
  const [selectedCustomer, setSelectedCustomer] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const customers = [
    { id: 1, name: 'John Doe', address: '123 Main St', phoneNumber: '555-1234' },
    { id: 2, name: 'Jane Smith', address: '456 Elm St', phoneNumber: '555-5678' },
    { id: 3, name: 'Mark Johnson', address: '789 Oak St', phoneNumber: '555-9012' },
    // Add more customer objects as needed
  ];

  const handleViewDetails = (customer) => {
    setSelectedCustomer(customer);
    setIsModalOpen(true);
  };

  const handleModalClose = () => {
    setIsModalOpen(false);
  };


  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSaveCustomer = (updatedCustomer) => {
    // Find the index of the updated customer in the customers array
    const index = customers.findIndex((customer) => customer.id === updatedCustomer.id);

    if (index !== -1) {
      // Create a new customers array with the updated customer
      const updatedCustomers = [
        ...customers.slice(0, index),
        updatedCustomer,
        ...customers.slice(index + 1)
      ];

      // Update the customers array in the state
      setSelectedCustomer(updatedCustomer);
      // You may also update the customers state if necessary
      // setCustomers(updatedCustomers);
    }

    setIsModalOpen(false);
  };

  return (
    <div>
      <h2>Customer List</h2>
      <table className="customer-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Address</th>
            <th>Phone Number</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {customers.map((customer) => (
            <tr key={customer.id}>
              <td>{customer.id}</td>
              <td>{customer.name}</td>
              <td>{customer.address}</td>
              <td>{customer.phoneNumber}</td>
              <td>
                <button onClick={() => handleViewDetails(customer)}>View/Edit</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {isModalOpen && selectedCustomer && (
        <Modal onClose={handleModalClose} customer={selectedCustomer} onSave={handleSaveCustomer}>
          <h3>Customer Details</h3>
          <p>ID: {selectedCustomer.id}</p>
          <p>Name: {selectedCustomer.name}</p>
          <p>Address: {selectedCustomer.address}</p>
          <p>Phone Number: {selectedCustomer.phoneNumber}</p>
          {/* Add editable fields and update logic here */}
        </Modal>
      )}
    </div>
  );
};

export default CustomerList;
