import React, { useState, useEffect } from 'react';
import Modal from './Modal';
import './Customer.css';
import Papa from 'papaparse';

const CustomerList = () => {
  const [selectedCustomer, setSelectedCustomer] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [customers, setCustomers] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const response = await fetch('../assets/sample_customers.csv');
      const csvData = await response.text();
      const parsedData = Papa.parse(csvData, { header: true }).data;
      setCustomers(parsedData);
    };

    fetchData();
  }, []);

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const filteredCustomers = customers.filter((customer) =>
    customer['Number of employees'] && customer['Number of employees'].includes(searchTerm)
  );

  const handleViewDetails = (customer) => {
    setSelectedCustomer(customer);
    setIsModalOpen(true);
  };

  const handleModalClose = () => {
    setIsModalOpen(false);
  };

  const handleSaveCustomer = (updatedCustomer) => {
    const index = customers.findIndex((customer) => customer['Organization Id'] === updatedCustomer['Organization Id']);

    if (index !== -1) {
      const updatedCustomers = [
        ...customers.slice(0, index),
        updatedCustomer,
        ...customers.slice(index + 1),
      ];

      setSelectedCustomer(updatedCustomer);
      setCustomers(updatedCustomers);
    }

    setIsModalOpen(false);
  };

  return (
    <div>
      <h2>Customer List</h2>
      <input
        type="text"
        placeholder="Search by number of employees"
        value={searchTerm}
        onChange={handleSearch}
      />
      <table className="customer-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Website</th>
            <th>Country</th>
            <th>Description</th>
            <th>Founded</th>
            <th>Industry</th>
            <th>Number of Employees</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {filteredCustomers.map((customer) => (
            <tr key={customer['Organization Id']}>
              <td>{customer.Index}</td>
              <td>{customer.Name}</td>
              <td>{customer.Website}</td>
              <td>{customer.Country}</td>
              <td>{customer.Description}</td>
              <td>{customer.Founded}</td>
              <td>{customer.Industry}</td>
              <td>{customer['Number of employees']}</td>
              <td>
                <button onClick={() => handleViewDetails(customer)}>View/Edit</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {isModalOpen && selectedCustomer && (
        <Modal onClose={handleModalClose} customer={selectedCustomer} onSave={handleSaveCustomer}>
          <h3>Customer Details</h3>
          <p>ID: {selectedCustomer.Index}</p>
          <p>Name: {selectedCustomer.Name}</p>
          <p>Website: {selectedCustomer.Website}</p>
          <p>Country: {selectedCustomer.Country}</p>
          <p>Description: {selectedCustomer.Description}</p>
          <p>Founded: {selectedCustomer.Founded}</p>
          <p>Industry: {selectedCustomer.Industry}</p>
          <p>Number of Employees: {selectedCustomer['Number of employees']}</p>
          {/* Add editable fields and update logic here */}
        </Modal>
      )}
    </div>
  );
};

export default CustomerList;
