import React, { useState } from 'react';
import Modal from './Modal';
import './Customer.css';

const CustomerList = () => {
    const [selectedCustomer, setSelectedCustomer] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [searchTerm, setSearchTerm] = useState('');
    const [customers, setCustomers] = useState([
        { id: 1, name: 'John Doe', address: '123 Main St', phoneNumber: '555-1234' },
        { id: 2, name: 'Jane Smith', address: '456 Elm St', phoneNumber: '555-5678' },
        { id: 3, name: 'Mark Johnson', address: '789 Oak St', phoneNumber: '555-9012' },
        { id: 4, name: 'Sarah Brown', address: '567 Maple Ave', phoneNumber: '555-4321' },
        { id: 5, name: 'Michael Davis', address: '890 Pine Rd', phoneNumber: '555-8765' },
        { id: 6, name: 'Emily Wilson', address: '321 Oakwood Ln', phoneNumber: '555-2109' },
        { id: 7, name: 'David Anderson', address: '654 Elmwood Dr', phoneNumber: '555-9012' },
        { id: 8, name: 'Jessica Taylor', address: '987 Spruce St', phoneNumber: '555-3456' },
        { id: 9, name: 'Daniel Moore', address: '234 Cedar Ave', phoneNumber: '555-7890' },
        { id: 10, name: 'Jennifer Lee', address: '876 Birch Rd', phoneNumber: '555-2345' },
        { id: 11, name: 'Matthew Clark', address: '109 Pinecone Ln', phoneNumber: '555-6789' },
        { id: 12, name: 'Ava Lewis', address: '432 Oakdale Dr', phoneNumber: '555-0123' },
        { id: 13, name: 'William Wright', address: '765 Maplewood Ave', phoneNumber: '555-4567' },
        { id: 14, name: 'Olivia Walker', address: '210 Elmwood Ct', phoneNumber: '555-8901' },
        { id: 15, name: 'James Hall', address: '543 Cedarwood Rd', phoneNumber: '555-2345' },
        { id: 16, name: 'Sophia Allen', address: '876 Pinehurst Blvd', phoneNumber: '555-6789' },
        { id: 17, name: 'Benjamin Hill', address: '109 Maple Dr', phoneNumber: '555-0123' },
        { id: 18, name: 'Emma Turner', address: '432 Elmwood Ave', phoneNumber: '555-4567' },
        { id: 19, name: 'Alexander Baker', address: '765 Oakdale Ln', phoneNumber: '555-8901' },
        { id: 20, name: 'Mia Garcia', address: '210 Cedarwood Ct', phoneNumber: '555-2345' },
        { id: 21, name: 'Henry Young', address: '543 Maplewood Rd', phoneNumber: '555-6789' },
        { id: 22, name: 'Charlotte Martinez', address: '876 Pinehurst Dr', phoneNumber: '555-0123' },
        { id: 23, name: 'Sebastian Clark', address: '109 Oakwood Ave', phoneNumber: '555-4567' },
        { id: 24, name: 'Amelia Scott', address: '432 Elm Rd', phoneNumber: '555-8901' },
        { id: 25, name: 'Jack Hill', address: '765 Maple Dr', phoneNumber: '555-2345' },
        { id: 26, name: 'Lily Wright', address: '210 Elmwood Ave', phoneNumber: '555-6789' },
        { id: 27, name: 'Owen Turner', address: '543 Cedar Ln', phoneNumber: '555-0123' },
        { id: 28, name: 'Ella Baker', address: '876 Pine St', phoneNumber: '555-4567' },
        { id: 29, name: 'Jackson Garcia', address: '109 Oakwood Ct', phoneNumber: '555-8901' },
        { id: 30, name: 'Scarlett Young', address: '432 Elmwood Blvd', phoneNumber: '555-2345' }
    ]);

    const handleSearch = (event) => {
        setSearchTerm(event.target.value);
    };

    const filteredCustomers = customers
        .filter((customer) => customer.phoneNumber.includes(searchTerm))
        .slice(0, 10); // Limit to the first 10 customers

    const handleViewDetails = (customer) => {
        setSelectedCustomer(customer);
        setIsModalOpen(true);
    };

    const handleModalClose = () => {
        setIsModalOpen(false);
    };

    const handleSaveCustomer = (updatedCustomer) => {
        setCustomers((prevCustomers) => {
            const updatedCustomers = prevCustomers.map((customer) =>
                customer.id === updatedCustomer.id ? updatedCustomer : customer
            );
            return updatedCustomers;
        });

        setIsModalOpen(false);
    };

    const goToNextPage = () => {
        setCurrentPage((prevPage) => prevPage + 1);
      };

      const goToPreviousPage = () => {
        setCurrentPage((prevPage) => prevPage - 1);
      };

    return (
        <div>
            <div>
                <h2>Customer List</h2>
                <input
                    type="text"
                    placeholder="Search by phone number"
                    value={searchTerm}
                    onChange={handleSearch}
                />
            </div>
            <table className="customer-table">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Name</th>
                        <th>Address</th>
                        <th>Phone Number</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {filteredCustomers.map((customer) => (
                        <tr key={customer.id}>
                            <td>{customer.id}</td>
                            <td>{customer.name}</td>
                            <td>{customer.address}</td>
                            <td>{customer.phoneNumber}</td>
                            <td>
                                <button onClick={() => handleViewDetails(customer)}>View/Edit</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            {/* Add arrow to go to the next page */}
            {filteredCustomers.length > 10 && (
                <button onClick={() => setCustomers(customers.slice(10))}>Next Page</button>
            )}

            {isModalOpen && selectedCustomer && (
                <Modal onClose={handleModalClose} customer={selectedCustomer} onSave={handleSaveCustomer}>
                    <h3>Customer Details</h3>
                    <p>ID: {selectedCustomer.id}</p>
                    <p>Name: {selectedCustomer.name}</p>
                    <p>Address: {selectedCustomer.address}</p>
                    <p>Phone Number: {selectedCustomer.phoneNumber}</p>
                    {/* Add editable fields and update logic here */}
                </Modal>
            )}
        </div>
    );
};

export default CustomerList;
